.theme-dark [class*=disabledPointerEvents],
.theme-light [class*=disabledPointerEvents] {
  --background-floating: #313244;
  --background-tertiary: #242437;
}
.theme-dark [class*=disabledPointerEvents] svg[class^=activityIcon-],
.theme-light [class*=disabledPointerEvents] svg[class^=activityIcon-] {
  color: #a6adc8;
}
.theme-dark [class*=disabledPointerEvents] [class*=tooltipBrand-] [class*=tooltipText-],
.theme-light [class*=disabledPointerEvents] [class*=tooltipBrand-] [class*=tooltipText-] {
  color: #11111b;
}

/* ==UserStyle==
@name YouTube Catppuccin-Mocha
@namespace github.com/catppuccin/YouTube
@homepageURL https://github.com/catppuccin/YouTube
@version 1.3.7
@updateURL https://github.com/catppuccin/YouTube/raw/main/src/YouTubeCatppuccinMocha.user.css
@description Soothing pastel theme for YouTube
@author elkrien based on RaitaroH Youtube-DeepDark (github.com/RaitaroH/YouTube-DeepDark)
@license GNU-V3.0

@preprocessor stylus

@var select stylus-deepdark-style "Preset themes" [
	"Catppuccin-Mocha-Green",
	"Catppuccin-Mocha-Flamingo",
	"Catppuccin-Mocha-Mauve",
	"Catppuccin-Mocha-Pink",
	"Catppuccin-Mocha-Lavender",
	"Catppuccin-Mocha-Red",
	"Catppuccin-Mocha-Peach",
	"Catppuccin-Mocha-Yellow",
	"Catppuccin-Mocha-Teal",
	"Catppuccin-Mocha-Blue",
	"Catppuccin-Mocha-Sky",
    "Catppuccin-Mocha-Maroon",
    "Catppuccin-Mocha-Sapphire",
    "Catppuccin-Mocha-Rosewater",
	"Custom"
]

@var checkbox join-color "Use dark join button" 1
@var checkbox subscribe-color "Use dark subscribe button" 0

@var text scrollbar_width "Scrollbar width" 6px
@var color scrollbar_track "Scrollbar background" "rgba(0,0,0, 0)"

@var color mainColor "Custom main color" "#89B4FA"
@var color mainBackground "Custom main background" "#1E1E2E"
@var color secondBackground "Custom second background" "#181825"
@var color playerBackground "Custom player background" "#11111B"
@var color hoverBackground "Custom hover background" "#313244"
@var color mainText "Custom main text" "#CDD6F4"
@var color dimmerText "Custom second text" "#9399B2"
@var color colorShadow "Custom shadow color" "rgba(56, 60, 74, .3)"

==/UserStyle== */
@-moz-document regexp('https?:\\/\\/(?!gaming\\.)(?!music\\.)(?!tv\\.)(?!artists\\.)(?!studio\\.).*youtube(?!kids\\.).*\\.com\\/(?!premium|howyoutubeworks).*$') {

    /* Author     : Theme made by RaitaroH & modified for Catppuccin by elkrien */
    /* Co-authors : https://github.com/MechaLynx https://github.com/MaximeRF */
    /* Home       : https://github.com/catppuccin/YouTube */
    /* Licence    : GNU General Public License v3.0 */
    /*Main color variables*/
    :root {
        if stylus-deepdark-style=="Catppuccin-Mocha-Green" {
            --main-color: #F43E5C;
            --main-background: #1E1E2E;
            --second-background: #181825;
            --player-background: #11111B;
            --hover-background: #313244;
            --main-text: #CDD6F4;
            --dimmer-text: #9399B2;
            --shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
        }

        else if stylus-deepdark-style=="Catppuccin-Mocha-Lavender" {
            --main-color: #B4BEFE;
            --main-background: #1E1E2E;
            --second-background: #181825;
            --player-background: #11111B;
            --hover-background: #313244;
            --main-text: #CDD6F4;
            --dimmer-text: #9399B2;
            --shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
        }

        else if stylus-deepdark-style=="Catppuccin-Mocha-Flamingo" {
            --main-color: #F2CDCD;
            --main-background: #1E1E2E;
            --second-background: #181825;
            --player-background: #11111B;
            --hover-background: #313244;
            --main-text: #CDD6F4;
            --dimmer-text: #9399B2;
            --shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
        }

        else if stylus-deepdark-style=="Catppuccin-Mocha-Mauve" {
            --main-color: #CBA6F7;
            --main-background: #1E1E2E;
            --second-background: #181825;
            --player-background: #11111B;
            --hover-background: #313244;
            --main-text: #CDD6F4;
            --dimmer-text: #9399B2;
            --shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
        }

        else if stylus-deepdark-style=="Catppuccin-Mocha-Pink" {
            --main-color: #F5C2E7;
            --main-background: #1E1E2E;
            --second-background: #181825;
            --player-background: #11111B;
            --hover-background: #313244;
            --main-text: #CDD6F4;
            --dimmer-text: #9399B2;
            --shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
        }

        else if stylus-deepdark-style=="Catppuccin-Mocha-Red" {
            --main-color: #F38BA8;
            --main-background: #1E1E2E;
            --second-background: #181825;
            --player-background: #11111B;
            --hover-background: #313244;
            --main-text: #CDD6F4;
            --dimmer-text: #9399B2;
            --shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
        }

        else if stylus-deepdark-style=="Catppuccin-Mocha-Peach" {
            --main-color: #FAB387;
            --main-background: #1E1E2E;
            --second-background: #181825;
            --player-background: #11111B;
            --hover-background: #313244;
            --main-text: #CDD6F4;
            --dimmer-text: #9399B2;
            --shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
        }

        else if stylus-deepdark-style=="Catppuccin-Mocha-Yellow" {
            --main-color: #F9E2AF;
            --main-background: #1E1E2E;
            --second-background: #181825;
            --player-background: #11111B;
            --hover-background: #313244;
            --main-text: #CDD6F4;
            --dimmer-text: #9399B2;
            --shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
        }

        else if stylus-deepdark-style=="Catppuccin-Mocha-Teal" {
            --main-color: #94E2D5;
            --main-background: #1E1E2E;
            --second-background: #181825;
            --player-background: #11111B;
            --hover-background: #313244;
            --main-text: #CDD6F4;
            --dimmer-text: #9399B2;
            --shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
        }

        else if stylus-deepdark-style=="Catppuccin-Mocha-Blue" {
            --main-color: #89B4FA;
            --main-background: #1E1E2E;
            --second-background: #181825;
            --player-background: #11111B;
            --hover-background: #313244;
            --main-text: #CDD6F4;
            --dimmer-text: #9399B2;
            --shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
        }

        else if stylus-deepdark-style=="Catppuccin-Mocha-Sky" {
            --main-color: #89DCEB;
            --main-background: #1E1E2E;
            --second-background: #181825;
            --player-background: #11111B;
            --hover-background: #313244;
            --main-text: #CDD6F4;
            --dimmer-text: #9399B2;
            --shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
        }

        else if stylus-deepdark-style=="Catppuccin-Mocha-Maroon" {
            --main-color: #EBA0AC;
            --main-background: #1E1E2E;
            --second-background: #181825;
            --player-background: #11111B;
            --hover-background: #313244;
            --main-text: #CDD6F4;
            --dimmer-text: #9399B2;
            --shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
        }

        else if stylus-deepdark-style=="Catppuccin-Mocha-Sapphire" {
            --main-color: #74C7EC;
            --main-background: #1E1E2E;
            --second-background: #181825;
            --player-background: #11111B;
            --hover-background: #313244;
            --main-text: #CDD6F4;
            --dimmer-text: #9399B2;
            --shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
        }

        else if stylus-deepdark-style=="Catppuccin-Mocha-Rosewater" {
            --main-color: #F5E0DC;
            --main-background: #1E1E2E;
            --second-background: #181825;
            --player-background: #11111B;
            --hover-background: #313244;
            --main-text: #CDD6F4;
            --dimmer-text: #9399B2;
            --shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
        }

        else if stylus-deepdark-style=="Custom" {
            --main-color: mainColor;
            --main-background: mainBackground;
            --second-background: secondBackground;
            --player-background: playerBackground;
            --hover-background: hoverBackground;
            --main-text: mainText;
            --dimmer-text: dimmerText;
            --shadow: 0 1px 0.5px colorShadow;
        }
    }

    /*Error page*/
    #error-page {
        background-color: var(--main-background) !important;
        color: var(--main-text) !important;
    }

    * {
        scrollbar-color: var(--main-color) scrollbar_track !important;
        scrollbar-width: thin !important;
        --yt-spec-text-primary: var(--main-text) !important;
        --yt-spec-text-secondary: var(--dimmer-text) !important;
        --paper-spinner-color: var(--dimmer-text) !important;

        --paper-input-container-color: var(--dimmer-text) !important;

        --paper-radio-button-unchecked-color: var(--dimmer-text) !important;
        --paper-radio-button-unchecked-background-color: transparent !important;
        --paper-radio-button-checked-color: var(--main-color) !important;
        --paper-radio-button-checked-ink-color: var(--main-color) !important;
        --paper-radio-button-unchecked-ink-color: var(--main-color) !important;

        --paper-checkbox-unchecked-color: var(--dimmer-text) !important;
        --paper-checkbox-checked-color: var(--main-color) !important;
        --paper-checkbox-checkmark-color: none !important;
        --paper-checkbox-checked-ink-color: var(--main-color) !important;
        --paper-checkbox-unchecked-ink-color: var(--main-color) !important;
        --yt-spec-call-to-action: var(--main-color) !important;
        --yt-spec-suggested-action: var(--hover-background) !important;
        --yt-spec-outline: var(--hover-background) !important;
        --yt-spec-brand-button-background: var(--main-color) !important;
        /*in incognito this may be used as subscribe button but is inconsistent .yt-spec-button-shape-next--brand.yt-spec-button-shape-next--filled*/
