/* ==UserStyle==
@name           Youtube Music Catppuccin
@namespace      github.com/catppuccin/youtubemusic
@version        0.3.0
@description    Soothing pastel theme for Youtube Music
@author         Catppuccin
@preprocessor   stylus
@var select theme "Theme" ["Latte", "Frappe", "Macchiato", "Mocha*"]
@var select accent-color "Accent" ["Rosewater*", "Flamingo", "Pink", "Mauve", "Red", "Maroon", "Peach", "Yellow", "Green", "Teal", "Blue", "Sapphire", "Sky", "Lavender", "Text"]
==/UserStyle== */

@-moz-document domain("music.youtube.com") {

    if (theme == "Latte") {
        @import url("https://catppuccin.github.io/youtubemusic/src/latte.css");
    } else if (theme == "Frappe") {
        @import url("https://catppuccin.github.io/youtubemusic/src/frappe.css");
    } else if (theme == "Macchiato") {
        @import url("https://catppuccin.github.io/youtubemusic/src/macchiato.css");
    } else if (theme == "Mocha") {
        @import url("https://catppuccin.github.io/youtubemusic/src/mocha.css");
    }

    :root {
        --ctp-red: #f43e5c !important;
    }

    html:not(.style-scope) {    
        if (accent-color=="Rosewater") {
          --ctp-accent: var(--ctp-rosewater) !important;
      } else if (accent-color=="Flamingo") {
          --ctp-accent: var(--ctp-flamingo) !important;
      } else if (accent-color=="Pink") {
          --ctp-accent: var(--ctp-pink) !important;
      } else if (accent-color=="Mauve") {
          --ctp-accent: var(--ctp-mauve) !important;
      } else if (accent-color=="Red") {
          --ctp-accent: var(--ctp-red) !important;
      } else if (accent-color=="Maroon") {
          --ctp-accent: var(--ctp-maroon) !important;
      } else if (accent-color=="Peach") {
          --ctp-accent: var(--ctp-peach) !important;
      } else if (accent-color=="Yellow") {
          --ctp-accent: var(--ctp-yellow) !important;
      } else if (accent-color=="Green") {
          --ctp-accent: var(--ctp-green) !important;
      } else if (accent-color=="Teal") {
          --ctp-accent: var(--ctp-teal) !important;
      } else if (accent-color=="Blue") {
          --ctp-accent: var(--ctp-blue) !important;
      } else if (accent-color=="Sapphire") {
          --ctp-accent: var(--ctp-sapphire) !important;
      } else if (accent-color=="Sky") {
          --ctp-accent: var(--ctp-sky) !important;
      } else if (accent-color=="Lavender") {
          --ctp-accent: var(--ctp-lavender) !important;
      }  else if (accent-color=="Text") {
          --ctp-accent: var(--ctp-text) !important;
      }
    }


    ytmusic-player-queue-item[play-button-state="playing"] {
        border-left: 4px solid var(--ctp-red);
    }
    ytmusic-player-queue-item ytmusic-background-overlay-renderer {
        opacity: 0.5 !important;
    }
    ytmusic-player-queue-item ytmusic-play-button-renderer {
        div.ytmusic-play-button-renderer {
            --ctp-text: transparent;
            --ctp-accent: transparent;
        }

        svg {
            fill: white;
            filter: drop-shadow(1px 2px 2px rgba(black, 0.7));
        }
    }
}

/* ==UserStyle==
@name NixOS Wiki Catppuccin
@namespace github.com/catppuccin/userstyles/styles/nixos.wiki
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/nixos.wiki
@version 0.0.6
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/nixos.wiki/catppuccin.user.css
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Anixos.wiki
@description Soothing pastel theme for NixOS Wiki
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:FrappÃ©", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:FrappÃ©", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve*", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */

@-moz-document domain('nixos.wiki') {
  @import url("https://python.catppuccin.com/pygments/catppuccin-variables.important.css");

  @media (prefers-color-scheme: light) {
    :root {
      #catppuccin(@lightFlavor, @accentColor);
    }
  }
  @media (prefers-color-scheme: dark) {
    :root {
      #catppuccin(@darkFlavor, @accentColor);
    }
  }

  #catppuccin(@lookup, @accent) {
    @rosewater: @catppuccin[@@lookup][@rosewater];
    @flamingo: @catppuccin[@@lookup][@flamingo];
    @pink: @catppuccin[@@lookup][@pink];
    @mauve: @catppuccin[@@lookup][@mauve];
    @red: @catppuccin[@@lookup][@red];
    @maroon: @catppuccin[@@lookup][@maroon];
    @peach: @catppuccin[@@lookup][@peach];
    @yellow: @catppuccin[@@lookup][@yellow];
    @green: @catppuccin[@@lookup][@green];
    @teal: @catppuccin[@@lookup][@teal];
    @sky: @catppuccin[@@lookup][@sky];
    @sapphire: @catppuccin[@@lookup][@sapphire];
    @blue: @catppuccin[@@lookup][@blue];
    @lavender: @catppuccin[@@lookup][@lavender];
    @text: @catppuccin[@@lookup][@text];
    @subtext1: @catppuccin[@@lookup][@subtext1];
    @subtext0: @catppuccin[@@lookup][@subtext0];
    @overlay2: @catppuccin[@@lookup][@overlay2];
    @overlay1: @catppuccin[@@lookup][@overlay1];
    @overlay0: @catppuccin[@@lookup][@overlay0];
    @surface2: @catppuccin[@@lookup][@surface2];
    @surface1: @catppuccin[@@lookup][@surface1];
    @surface0: @catppuccin[@@lookup][@surface0];
    @base: @catppuccin[@@lookup][@base];
    @mantle: @catppuccin[@@lookup][@mantle];
    @crust: @catppuccin[@@lookup][@crust];
    @accent-color: @catppuccin[@@lookup][@@accent];

    --ctp-rosewater: @rosewater;
    --ctp-flamingo: @flamingo;
    --ctp-pink: @pink;
    --ctp-mauve: @mauve;
    --ctp-red: @red;
    --ctp-maroon: @maroon;
    --ctp-peach: @peach;
    --ctp-yellow: @yellow;
    --ctp-green: @green;
    --ctp-teal: @teal;
    --ctp-sky: @sky;
    --ctp-sapphire: @sapphire;
    --ctp-blue: @blue;
    --ctp-lavender: @lavender;
    --ctp-text: @text;
    --ctp-subtext1: @subtext1;
    --ctp-subtext0: @subtext0;
    --ctp-overlay2: @overlay2;
    --ctp-overlay1: @overlay1;
    --ctp-overlay0: @overlay0;
    --ctp-surface2: @surface2;
    --ctp-surface1: @surface1;
    --ctp-surface0: @surface0;
    --ctp-base: @base;
    --ctp-mantle: @mantle;
    --ctp-crust: @crust;

    color-scheme: if(@lookup = latte, light, dark);

    ::selection {
      background-color: fade(@accent-color, 30%);
    }

    input,
    textarea {
      &::placeholder {
        color: @subtext0 !important;
      }
    }

    &[data-darkreader-scheme="dark"] body::before {
      content: "Please turn off DarkReader for this domain when Catppuccin for NixOS Wiki is active.";
      background: @red;
      box-shadow:
        0 4px 16px fade(@crust, 10%),
        0 8px 32px fade(@crust, 5%);
      color: @crust;
      position: fixed;
      top: 0;
      right: 0;
      z-index: 99999;
      max-width: 24rem;
      padding: 0.75rem;
      margin: 0.5rem;
      border-radius: 5px;
    }

    body {
      background-color: @base;
    }
    hr {
      border-color: @surface2;
    }
    .navbar-default {
      color: @text;
      background-color: @mantle !important;
      background-image: none !important;
      border-color: transparent !important;

      .navbar-brand {
        color: @blue;
        &:hover {
          color: @sky;
        }
      }

      li {
        a {
          color: @text !important;
          &:hover {
            color: @subtext0 !important;
          }
        }
        &.nav.open > a,
        ul {
          background-color: @surface0 !important;
          background-image: none !important;
          a:hover {
            background-color: fade(@surface1, 50%) !important;
            background-image: none !important;
          }
        }
      }
      .active > a {
        color: @crust !important;
        &:hover {
          color: @base !important;
        }
        background: @blue !important;
      }
    }

    .search-query.form-control {
      color: @text;
      background-color: @surface0;
      border-color: @surface0;
    }

    * {
      color: @text;
    }

    a {
      color: @blue;
      &:hover {
        color: @sky;
      }
      &.new {
        color: @red;
        &:hover {
          color: @maroon;
        }
      }
    }

    .panel {
      background-color: @mantle;
      border-color: @mantle;
      .panel-heading {
        color: @text;
        background-color: @surface0 !important;
        border-bottom-color: @surface2;
        img {
          & when not (@lookup = latte) {
            filter: invert(100%);
          }
        }
      }
    }

    .btn-primary {
      color: @crust;
      background-color: @blue !important;
      background-image: none !important;
      border-color: @crust !important;
      &:hover {
        color: @crust !important;
        background-color: @sky !important;
        background-image: none !important;
      }
    }

    table {
      &,
      * {
        color: @text;
        border-color: @overlay0 !important;
        background-color: @mantle;
      }
      th {
        &,
        * {
          background-color: @surface0 !important;
        }
      }
    }

    blockquote,
    cite {
      border-left-color: @pink !important;
    }

    cite a {
      color: @pink;
      &:hover {
        color: @rosewater;
      }
    }

    pre,
    code {
      box-shadow: none !important;
      background-color: @mantle;
      border-color: @crust;
    }
    pre {
      color: @text;
    }
    code {
      color: @rosewater;
    }

    *[style*="background-color: #C1E5FF"] {
      color: @text !important;
      background-color: fade(@blue, 15%) !important;
      border-color: @blue !important;
    }

    *[style*="background-color: #FFD0B7"] {
      color: @text !important;
      background-color: fade(@peach, 15%) !important;
      border-color: @peach !important;
    }

    *[style*="background: #F6F6F6"] {
      color: @text !important;
      background-color: fade(@overlay0, 15%) !important;
      border-color: @overlay0 !important;
    }

    .suggestions {
      .suggestions-result {
        background-color: @mantle;
        &:hover {
          background-color: mix(@base, @mantle, 50%);
        }
        color: @text;
        .highlight {
          color: @accent-color;
        }
      }
      .suggestions-results {
        border-color: @surface0 !important;
      }
      .suggestions-special {
        border-color: @surface0 !important;
        background-color: @mantle;
        &:hover {
          background-color: mix(@accent-color, @mantle, 15%);
        }
        .special-label {
          color: @subtext0;
        }
        .special-query {
          color: @text;
        }
      }
    }

    .toc {
      li.active > a {
        border-left-color: @accent-color !important;
      }
      ul.nav a:hover {
        background: @accent-color !important;
        &,
        * {
          color: @crust !important;
        }
      }
    }

    .page-header {
      border-bottom-color: @overlay2;
    }

    #catlinks {
      background-color: @mantle;
      border-color: @mantle;
    }

    #footer {
      background-color: @mantle;
    }
  }
}

