      background-color: @red;
    }

    button:disabled,
    select:disabled,
    option:disabled,
    select[disabled] > option {
      color: @subtext0;
    }
    .applies-to input,
    .applies-to select {
      background: @base;
      border: 1px solid @surface0;
      color: @subtext0;
    }
    button {
