    --display-green-fgColor: @green;
    --display-teal-fgColor: @teal;
    --display-purple-fgColor: @mauve;
    --display-blue-fgColor: @blue;
    --display-pink-fgColor: @pink;
    --display-gray-fgColor: @subtext0;
    react-app[app-name="copilot-immersive-v1"],
