      .ansifg-r {
        color: var(--color-ansi-red);
      }
      .ansifg-y {
        color: var(--color-ansi-yellow);
      }
      .ansifg-g {
        color: var(--color-ansi-green);
      }
      .ansifg-b {
        color: var(--color-ansi-blue);
      }
      .ansifg-c {
        color: var(--color-ansi-cyan);
      }
      .ansifg-m {
        color: var(--color-ansi-magenta);
      }
      .ansifg-gr {
        color: var(--color-ansi-gray);
      }
