/* ==UserStyle==
@name Hackage Catppuccin
@namespace github.com/catppuccin/userstyles/styles/hackage
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/hackage
@version 0.1.2
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/hackage/catppuccin.user.css
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Ahackage
@description Soothing pastel theme for Hackage
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:FrappÃ©", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:FrappÃ©", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve*", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */

@-moz-document domain("hackage.haskell.org"), domain("xmonad.github.io") {
  @media (prefers-color-scheme: light) {
    :root {
      #catppuccin(@lightFlavor, "pred", "crust", "red");
    }
  }
  @media (prefers-color-scheme: dark) {
    :root {
      #catppuccin(@darkFlavor, "pred", "crust", "red");
    }
  }

  #catppuccin(@lookup, @accent, @accent2, @accent3) {
    @rosewater: @catppuccin[@@lookup][@rosewater];
    @flamingo: @catppuccin[@@lookup][@flamingo];
    @pink: @catppuccin[@@lookup][@pink];
    @mauve: @catppuccin[@@lookup][@mauve];
    @red: @catppuccin[@@lookup][@red];
    @maroon: @catppuccin[@@lookup][@maroon];
    @peach: @catppuccin[@@lookup][@peach];
    @yellow: @catppuccin[@@lookup][@yellow];
    @green: @catppuccin[@@lookup][@green];
    @teal: @catppuccin[@@lookup][@teal];
    @sky: @catppuccin[@@lookup][@sky];
    @sapphire: @catppuccin[@@lookup][@sapphire];
    @blue: @catppuccin[@@lookup][@blue];
    @lavender: @catppuccin[@@lookup][@lavender];
    @text: @catppuccin[@@lookup][@text];
    @subtext1: @catppuccin[@@lookup][@subtext1];
    @subtext0: @catppuccin[@@lookup][@subtext0];
    @overlay2: @catppuccin[@@lookup][@overlay2];
    @overlay1: @catppuccin[@@lookup][@overlay1];
    @overlay0: @catppuccin[@@lookup][@overlay0];
    @surface2: @catppuccin[@@lookup][@surface2];
    @surface1: @catppuccin[@@lookup][@surface1];
    @surface0: @catppuccin[@@lookup][@surface0];
    @base: @catppuccin[@@lookup][@base];
    @mantle: @catppuccin[@@lookup][@mantle];
    @crust: @catppuccin[@@lookup][@crust];
    @accent-color: @catppuccin[@@lookup][@@accent];
    @accent-color2: @catppuccin[@@lookup][@@accent2];
    @accent-color3: @catppuccin[@@lookup][@@accent3];

    color-scheme: if(@lookup = latte, light, dark);

    ::selection {
      background-color: fade(@accent-color, 30%);
    }

    input,
    textarea {
      &::placeholder {
        color: @subtext0 !important;
      }
    }

    &,
    body {
      background-color: @base;
      color: @text;
    }

    a:hover {
      background-color: inherit;
    }

    a[href]:link {
      color: @accent-color;
    }
    a[href]:visited {
      color: @accent-color;
    }

    a[href].def:link,
    a[href].def:visited {
      color: @subtext0;
    }
    a[href].def:hover {
      color: average(@overlay2, @subtext0);
    }

    .caption,
    h1,
    h2,
    h3,
    h4,
    h5,
    h6,
    summary {
      color: @accent-color3;
      filter: none;
    }

    .collapser::before,
    .expander::before,
    .noexpander::before {
      color: @accent-color;
    }

    pre {
      background-color: @mantle;
      border-color: @surface1;
    }

    blockquote {
      background-color: @surface0;
      border-color: @accent-color;
    }

    .src {
      background-color: @mantle;
    }

    /*
     * Headers
     */

    #page-header,
    #package-header {
      background-color: @accent-color2;
      color: @surface0;
      border-color: shade(@accent-color2, 25%);
    }

    #page-header .caption,
    #package-header .caption,
    #page-header a:link,
    #page-header a:visited {
      color: @subtext0 !important;
    }

    #module-header .caption {
      color: @accent-color;
      border-color: @surface1;
    }

    #module-header {
      background-color: inherit;
    }

    #package-header {
      &,
      #page-menu {
        a:link,
        a:visited {
          color: @surface2;
        }
      }
      a:hover {
        background-color: transparent;
      }

      ul.links li {
        border-color: @overlay0;

        &:hover {
          background-color: transparent;
        }
      }
    }

    ul.links li form {
      input,
      button {
        color: @subtext0;
      }
    }

    /*
     * Style selectors (for old packages)
     */

    #style-menu {
      background-color: @mantle;

      li {
        &,
        a {
          color: @text !important;
        }
        + li {
          border-color: @overlay2;
        }
      }
    }

    /*
     * Versions
     */

    a.unpreferred[href]:link,
    a.unpreferred[href]:visited {
      color: @yellow;
    }

    a.deprecated[href]:link,
    a.deprecated[href]:visited {
      color: @red;
    }

    .versions {
      a.normal[href]:link,
      a.normal[href]:visited {
        color: @green;
      }
    }

    /*
     * Readme style
     */

    .embedded-author-content {
      border-color: @accent-color;
    }

    /*
     * Contents & synopsis
     */

    #contents-list {
      background-color: @mantle;
    }

    #table-of-contents {
      background-color: @base;
      border-color: @surface1;
    }

    #synopsis {
      summary {
        color: transparent;
      }

      ul {
        &,
        li.src {
          background-color: @surface0;
        }
      }
    }

    /*
     * Quick jump
     */

    #search p.error {
      color: @red;
    }

    .search input {
      background-color: @mantle;
    }

    #search-form,
    #search-results {
      box-shadow: 2px 2px 6px fade(@mantle, 20%);
    }

    #search-form {
      input {
        border-color: @accent-color;
      }
    }

    #search-results {
      border-color: @accent-color;

      & > ul > li {
        border-bottom-color: @overlay0;
      }
    }

    .search-module > ul > li > a[href].active-link {
      background-color: @surface2;
    }

    .search-result ul.subs::after {
      color: @subtext1;
    }

    .more-results,
    .more-results::before {
      color: @subtext0;
    }

    .keyboard-shortcuts th {
      color: @accent-color;
    }

    .key {
      background-color: @mantle;
      color: @text;
      border-color: @mantle;
    }

    /*
     * Dropdown
     */

    .dropdown-menu {
      background-color: @surface0;
      border-color: @surface0;

      button {
        background-color: @accent-color;
        color: @mantle;
        border-color: @surface2;

        &:hover,
        &:active {
          background-color: @accent-color;
          color: @mantle;
        }
      }
    }

    /*
     * Tables
     */

    table {
      &.info {
        background-color: @base;
        color: @subtext1;
        border-color: @surface1;
      }

      &.fancy {
        &,
        tr {
          border-color: @surface1;
        }
        tr.even td {
          background-color: @mantle;
        }
      }

      &.fancy,
      &.properties,
      &.dataTable.compact.fancy tbody {
        th,
        td {
          background-color: @base;
        }
      }
    }

    .doc {
      th,
      td {
        border-color: @surface1;
      }
      th {
        background-color: @mantle;
      }
    }

    div#table_length.dataTables_length,
    div#table_filter.dataTables_filter {
      color: @text;
    }

    div #description table {
      &,
      tr td {
        border-color: @surface1;
      }
      th {
        background-color: @mantle;
      }
    }

    /*
     * Source links
     */

    #interface {
      .src {
        .selflink .link {
          color: @overlay2;
        }
      }
      p.src .link {
        background-color: transparent;
        color: @overlay2;
        border-color: @overlay1;
      }

      span.fixity,
      span.rightedge {
        color: @overlay1;
        border-left-color: @overlay1;
      }
      span.fixity {
        color: @overlay1;
      }
    }

    /*
     * Paginator
     */

    .paginator {
      span {
        color: @text;
      }

      a {
        color: @overlay1;

        &[href]:link,
        &:link:hover {
          color: @accent-color;
        }
        &:visited,
        &:visited:hover {
          color: average(@overlay2, @accent-color);
        }
        &:link:hover,
        &:visited:hover {
          background-image: none;
          background-color: @surface1;
          border-color: transparent;
        }
      }

      .current,
      .current:hover {
        background-image: none;
        background-color: @surface0;
        color: @accent-color;
        border-color: transparent;
      }
    }

    .paginate_button {
      background-color: @overlay0;
    }

    /*
     * Search query
     */

    #browseTable th {
      border-color: @surface1;
    }

    #fatalError {
      color: @red;
    }

    input:invalid {
      border-color: @yellow;
      box-shadow: 0 0 10px @yellow;
    }

    input {
      background-color: @mantle;
      border-style: solid;
      border-color: @surface0;
      border-radius: 4px;
    }

    input[type="range"] {
      -moz-appearance: none;
      -webkit-appearance: none;
      appearance: none;
      background-color: @mantle;
      height: 0.2em !important;

      &::-moz-range-thumb,
      &::-webkit-slider-thumb {
        background-color: @text;
        border-color: @surface0;
        height: 1em !important;
      }
    }

    input[type="submit"] {
      color: @text;
    }

    input[type="range"]:hover,
    input[type="submit"]:hover {
      background-color: @crust;
    }

    /*
     * Others
     */

    .candidate-warn {
      background-color: @mantle;
      border-color: @red;
    }

    .candidate-info {
      background-color: @mantle;
      border-color: @blue;
    }

    .box {
      background-color: @base;
      border-color: @base;
    }

    div#modal #content,
    #search-results {
      background-color: @surface0;
    }

    .subs,
    .top > .doc,
    .subs > .doc {
      border-left-color: @surface1;
    }

    .subs .subs p.src {
      background-color: @mantle;
    }

    .top p.src {
      border-color: @surface0;
    }

    .warning,
    strong.warning {
      color: @red;
    }

    small.info,
    .flags-table .flag-disabled {
      color: @subtext0;
    }

    .text-button {
      color: @accent-color;
    }

    p.registration-email {
      border-color: @accent-color;
    }

    :target {
      background-image: linear-gradient(
        to bottom,
        transparent 0%,
        transparent 65%,
        average(@surface0, @yellow) 60%,
        average(@surface0, @yellow) 100%
      );
    }
    :target:hover {
      background-image: linear-gradient(
        to bottom,
        overlay(@surface0, @yellow) 0%,
        overlay(@surface0, @yellow) 100%
      );
    }

    #footer {
      background-color: @crust;
      color: @subtext1;
      border-color: @overlay0;
    }

    /*
     * MathJax
     */

    #MathJax_Message {
      background-color: @mantle;
      border-color: @surface1;
      color: @text;
    }

    #MathJax_About,
    #MathJax_Help,
    .MathJax_Menu {
      background-color: @surface0;
      color: @text;
      border-color: @accent-color;
      box-shadow: 0 10px 20px @mantle;
      -webkit-box-shadow: 0 10px 20px @mantle;
      -moz-box-shadow: 0 10px 20px @mantle;
    }

    #MathJax_About > span,
    #MathJax_HelpContent {
      background-color: @surface1 !important;
      color: @subtext1 !important;
      border-color: @overlay0 !important;
    }

    .MathJax_MenuArrow {
      color: @subtext1;
    }

    .MathJax_MenuRule {
      border-top-color: @overlay0;
    }

    .MathJax_MenuActive {
      background-color: @surface1;
      color: @text;
    }

    .MathJax_MenuDisabled {
      color: @overlay2;
    }

    .MathJax_MenuDisabled:focus,
    .MathJax_MenuLabel:focus {
      background-color: @surface1;
    }

    #MathJax_AboutClose,
    #MathJax_HelpClose {
      border-color: @surface2;
      color: @subtext0;
      span {
        background-color: @surface2;
      }

      &:hover {
        color: @text !important;
        border-color: @overlay0 !important;
        span {
          background-color: @overlay0 !important;
        }
      }
    }

    span.mathjax img when not (@lookup = latte) {
      filter: invert(100%);
    }

    /*
     * Sources
     */

    /* Old */

    .hs-keyglyph,
    .hs-layout {
      color: @overlay2;
    }
    .hs-comment a {
      color: @overlay0;
    }
    .hs-str,
    .hs-chr {
      color: @green;
    }

    /* Modern */

    .hs-identifier {
      color: @blue;
    }
    .hs-identifier.hs-type {
      color: @yellow;
    }
    .hs-keyword {
      color: @mauve;
    }
    .hs-string,
    .hs-char {
      color: @green;
    }
    .hs-number {
      color: @peach;
    }
    .hs-operator {
      color: @sky;
    }
    .hs-glyph,
    .hs-special {
      color: @overlay2;
    }
    .hs-comment {
      color: @overlay0;
    }
    .hs-pragma {
      color: @pink;
    }
    .hs-cpp {
      color: @pink;
    }

    pre a {
      &:link,
      &:visited {
        border-bottom-color: @surface0;
      }
      &:hover,
      &.hover-highlight {
        background-color: @surface0;
      }
    }

    span.annot {
      color: @text;

      &:hover {
        background-color: @surface1;
      }

      span.annottext {
        background-color: @surface0;
        border-color: @accent-color;
      }
    }
  }
}

